import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export class ShowBlogDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      blog: {}
    }
  }
  componentDidMount() {
    axios.get('http://localhost:5000/blogs/' + this.props.match.params.id).then(res => {
      this.setState({blog: res.data})
    }).catch(err => {
      console.log('Error from show blog details');
    })
  }
  onDeleteClick(id) {
    axios.delete('http://localhost:5000/blogs/' + id).then(res => { })
  }
  render() {
    return <div></div>;
  }
}

export default ShowBlogDetails;
