import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export class CreateBlog extends Component {
  constructor() {
    super();
    this.state = {
      title: '',
      image: '',
      body: '',
      created: '',
    };
    this.onHandleChange = this.onHandleChange(this);
    this.onHandleSubmit = this.onHandleSubmit(this);
  }

  onHandleChange(evt) {
    this.setState({ [evt.target.name]: evt.target.value });
  }

  onHandleSubmit(evt) {
    evt.preventDefault();
    const data = {
      title: this.state.title,
      image: this.state.image,
      body: this.state.body,
      created: this.state.created,
    };
    axios
      .post('http://localhost:5000/blogs', data)
      .then((res) => {
        this.setState({
          title: '',
          image: '',
          body: '',
          created: '',
        });
        this.props.history.push('/');
      })
      .catch((err) => {
        console.log('Error in create blog');
      });
  }

  render() {
    return (
      <div>
        <Link to="/">Show Blogs</Link>
        <h1>Add Blog</h1>
        <p>Create New Blog</p>
        <form  onSubmit={ this.onHandleSubmit}></form>
      </div>
    );
  }
}

export default CreateBlog;
